// src/util/detectRuntime.ts
function detectRuntime() {
  if (globalThis.navigator?.userAgent === "Cloudflare-Workers") {
    return "workerd";
  }
  if (globalThis.process?.release?.name === "node") {
    return "node";
  }
  return "unknown";
}

exports.detectRuntime = detectRuntime;
